ALTER FUNCTION [dbo].[FN_IsNullorEmptyorZero]
(
    @Value varchar(MAX),
    @ReturnValue varchar(MAX)
)
RETURNS varchar(MAX)
AS
BEGIN
    RETURN
	-- Function to combine isnull and isblank for local and central functionality. 
	
    CASE
        WHEN ISNULL(@Value, '') IN ( '',0)
              THEN
            @ReturnValue
        ELSE
            @Value
    END;
END;
GO
