                                                                                                                        --            ,dbo.FN_IsNullorEmptyorZero([IsInputPhotoRequired],16)) -- 16 
                                                                                                                        --            ,dbo.FN_IsNullorEmptyorZero([IsInputPhotoRequired],16))  --2020-09-04 doesn't work for the camera input
                                                                                                                        dbo.FN_IsNullorEmptyorZero([IsInputPhotoRequired],16)

                                    -- Authorisation?
