  EXEC dbo.SP_API_IPLODSetup_UIGroupGroupDetails   @operationId =0, @projectId =259,@appDeploymentMode='mining_fitters',@appCfgRefreshPoint=2,@FromDate='', @isPurge = @isPurge OUTPUT
 SELECT @isPurge AS isPurge


