				FROM
				 View_API_PostingSummary
				 WHERE Warehouse IN (SELECT Warehouse FROM dbo.FN_GetProjectWarehouses(@ProjectID)) 
				 AND (CreatedDate > DATEADD(DD,-1*@DaysOfHistoryForStores,@Today)
