		, ImageFileName 
		,AttributesJson

FROM
 [dbo].[View_ShiftSummary_ProdDrillHistory]
WHERE @projectId = ProjectId
AND date >  DATEADD(DAY, - @DaysOfHistoryForProdDrill,@Today)
end


