        SET @TypeAliasMethod = (SELECT  TOP 1 ConfigValue FROM dbo.FN_ProjSetup_TypeAlias_V2(@ProjectId,@Type,1,'TypeAliasMethod','DataV')); --  methods are    0 --> no Type Alias used, just mining and maint

