   DECLARE @ReportingBitwiseValue INT = (SELECT TOP 1 ConfigValue FROM dbo.FN_ProjSetup_TypeAlias_V2(@ProjectID,@Type,@TypeId,'DataSummaryReportingBitwiseFlag',@Mode));
