      + '  AND InputDate BETWEEN  ' + CHAR(39) + FORMAT(@StartDate, 'yyyy-MM-dd') + CHAR(39) + ' AND ' + CHAR(39) + FORMAT(@EndDAte, 'yyyy-MM-dd') + CHAR(39)
     ,IdentityFieldName = NULL --only used for  'UpdateOrInsert'



    SELECT
