     ,'GroupOrder' = 0 --CASE WHEN FeedbackAdvice= '1' THEN 0 ELSE 1 END
     ,NULL AS 'GroupColour'
     ,NULL AS 'ColourCode'

     ,DATE
     ,Shift
     ,Operator
