     ,TIME
     ,EquipID
      -- DisplayOrder,
     ,[TWI Question] = dbo.FN_StripHTML([TWI Question])
     ,UPPER(Answer) AS 'Answer'
     ,UPPER(Comments) AS 'Comments'
     ,StagingID
