          )

    --AND InputType <> 'Stat' --exclude hour meter readings from this view
    /*			AND (
						EquipmentID IN (
							SELECT ID
							FROM [dbo].[FN_SplitString](@EquipmentID)
							)
						OR @EquipmentID IS NULL
						) */

    ) AS tbl
    ORDER BY GroupOrder
    , Group1 DESC
    , MEtric

    , DATE DESC
    , Shift
    , Operator
    --,DisplayOrder
    , StagingID
  END


  ELSE
  IF (@Type = 'Safety'
