    AND @SpecialMCRHazard = 1)
  BEGIN
    --EXEC [dbo].[SP_ShiftSummary_QTypeInputs_v2] @projectID = '399', @Date = '2021-06-14', @Type = 'Safety', @Display = 'Hazard_Management', @OperatorID = '', @EquipmentID = ''
